$(document).ready((function(){$(".tabs > ul > li").on("click",(function(a){a.preventDefault();let t=$(this);t.siblings().removeClass("is-active"),t.addClass("is-active");let l="#content-"+t.data("content");$(l).siblings().css("display","none"),$(l).css("display","block")})),$("button[data-modal], a[data-modal]").on("click",(function(a){a.preventDefault();let t=$(this).data("modal");$("#"+t).addClass("is-active")})),$(".modal-close, .modal-background").on("click",(function(a){$(".modal").removeClass("is-active")}))}));