Vue.component("members-suggestion",{template:'<div>\n <input\n type="search"\n name="member_fullname"\n placeholder="Buscar miembro por nombre"\n list="members_suggested"\n class="input" \n v-model="suggesting"\n @input="membersSuggest()"\n />\n <datalist id="members_suggested">\n <option v-for="(member,index) in list" :key="index" :value="member.fullname"></option>\n </datalist>\n </div>',name:"MembersSuggestion",props:["route"],data:function(){return{suggesting:"",list:[]}},methods:{membersSuggest(){this.suggesting.length<3||fetch(this.route+this.suggesting,{headers:{"X-Requested-With":"XMLHttpRequest","Content-Type":"application/json",Accept:"application/json, text-plain, */*"},method:"get",credentials:"same-origin"}).then(e=>e.json()).then(e=>{this.list=e}).catch(e=>{console.log(e)})}}});const app=new Vue({el:"#app"});